INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Fried Green Tomatoes', '"You can also fry up red tomatoes with this recipe but make sure they are not over ripe or they will be mushy. Serve these tomatoes outside with a glass of iced tea one summer night and enjoy the sunset with someone you love."', 'https://bit.ly/2KFNsyl', '5', '15', 'whistle_stop91', '{"steps": ["Slice tomatoes 1/2 inch thick. Discard the ends.", "When water is boiled, put in the egg.", "In a large skillet, pour vegetable oil (enough so that there is 1/2 inch of oil in the pan) and heat over a medium heat. Place tomatoes into the frying pan in batches of 4 or 5, depending on the size of your skillet. Do not crowd the tomatoes, they should not touch each other. When the tomatoes are browned, flip and fry them on the other side. Drain them on paper towels."]}'::jsonb, '{"items": ["4 large green tomatoes", "2 eggs", "1/2 cup milk", "1 cup all-purpose flour", "1/2 cup cornmeal", "1/2 cup bread crumbs", "2 teaspoons coarse kosher salt", "1/4 teaspoon ground black pepper", "1 quart vegetable oil"]}'::jsonb);
INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Ken''s Perfect Hard Boiled Egg', '"Couldn''t sleep one night so I researched several ways to hard boil eggs and developed this process. While you do not taste the vinegar or salt, they both pull the skin from the eggshell so it peels easily without tearing or sticking. Family members call them "Ken''s Eggs". Enjoy!"', 'https://airfryer.cooking/wp-content/uploads/2018/01/HardboiledEggs-Large.jpg', '5', '20', 'egg_man53', '{"steps": ["Combine the salt, vinegar, and water in a large pot, and bring to a boil over high heat. Add the eggs one at a time, being careful not to crack them. Reduce the heat to a gentle boil, and cook for 14 minutes.", "Once the eggs have cooked, remove them from the hot water, and place into a container of ice water or cold, running water. Cool completely, about 15 minutes. Store in the refrigerator up to 1 week."]}'::jsonb, '{"items": ["1 tablespoon salt", "1/4 cup distilled white vinegar", "6 cups water", "8 eggs"]}'::jsonb);