INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Ken''s Perfect Hard Boiled Egg', '"Couldn''t sleep one night so I researched several ways to hard boil eggs and developed this process. While you do not taste the vinegar or salt, they both pull the skin from the eggshell so it peels easily without tearing or sticking. Family members call them "Ken''s Eggs". Enjoy!"', 'https://www.bowlofdelicious.com/wp-content/uploads/2015/11/Easy-to-Peel-Hard-Boiled-Eggs-square-500x500.jpg', '5', '20', 'egg_man53', '{"steps": ["1. Combine the salt, vinegar, and water in a large pot, and bring to a boil over high heat. Add the eggs one at a time, being careful not to crack them. Reduce the heat to a gentle boil, and cook for 14 minutes.", "2. Once the eggs have cooked, remove them from the hot water, and place into a container of ice water or cold, running water. Cool completely, about 15 minutes. Store in the refrigerator up to 1 week."]}'::jsonb, '{"items": ["1 tablespoon salt", "1/4 cup distilled white vinegar", "6 cups water", "8 eggs"]}'::jsonb);
INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Prosciutto e Melone', '"This easy and refreshing starter featuring cantaloupe and cured ham (Prosciutto for instance) can also be served as an appetizer: just cut the cantaloupe into bite-size pieces, wrap them with ham and spear them on skewers for a colorful snack! Use superior quality Italian ham for best results."', 'https://i.pinimg.com/564x/71/80/7b/71807bf16d629e8f78df14aaead8267d--schick-photos.jpg', '5', '10', 'Cat_Elsby', '{"steps": ["1. Stack the melon, prosciutto and Arugula; secure with toothpick.", "2. Drizzle with honey."]}'::jsonb, '{"items": ["1/4 lbs. prosciutto", "12 bite-sized pieces of melon", "Bunch of Arugula", "Honey for drizzle"]}'::jsonb);
INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Red Cabbage Salad with Apple', '"My Sister Alla''s Red Cabbage Salad with Apple is loaded with the best Autumn Ingredients. She sneaks sauerkraut into her salad, making it like a red cabbage slaw. One of our Favorite Red Cabbage Recipes"', 'https://natashaskitchen.com/wp-content/uploads/2018/10/Red-Cabbage-Salad-with-Apple-4-500x500.jpg', '20', '20', 'Anna Pavlova', '{"steps": ["1. Finely chop cabbage and transfer it to a large mixing bowl.", "2. Thoroughly squeeze sauerkraut by the fist-fulls to remove excess moisture until you have 1/2 cup of squeezed sauerkraut then add it to the salad bowl.", "3. Add sliced cucumber and granny smith apple, chopped dill and green onion.", "4. Drizzle the salad with 4 Tbsp extra virgin olive oil and 1 Tbsp white vinegar and 1/2 tsp sea salt*. Toss the salad well to combine and add more salt and/or vinegar to taste.", "*Serving Tip: Add salt until just before serving since it will cause the cucumbers to wilt."]}'::jsonb, '{"items": ["1/2 medium red cabbage (6 cups finely chopped)", "1/2 cup sauerkraut well drained", "1/2 English cucumber sliced", "1 granny smith apple sliced into thin bite-sized pieces", "1/2 cup green onion chopped", "1 Tbsp dill finely chopped", "4 Tbsp extra virgin olive oil", "1 Tbsp white vinegar or to taste", "1/2 tsp sea salt or to taste"]}'::jsonb);
INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Fried Green Tomatoes', '"You can also fry up red tomatoes with this recipe but make sure they are not over ripe or they will be mushy. Serve these tomatoes outside with a glass of iced tea one summer night and enjoy the sunset with someone you love."', 'https://spicysouthernkitchen.com/wp-content/uploads/fried-green-tomatoes-1.jpg', '5', '15', 'whistle_stop91', '{"steps": ["1. Slice tomatoes 1/2 inch thick. Discard the ends.", "2. When water is boiled, put in the egg.", "3. In a large skillet, pour vegetable oil (enough so that there is 1/2 inch of oil in the pan) and heat over a medium heat. Place tomatoes into the frying pan in batches of 4 or 5, depending on the size of your skillet. Do not crowd the tomatoes, they should not touch each other. When the tomatoes are browned, flip and fry them on the other side. Drain them on paper towels."]}'::jsonb, '{"items": ["4 large green tomatoes", "2 eggs", "1/2 cup milk", "1 cup all-purpose flour", "1/2 cup cornmeal", "1/2 cup bread crumbs", "2 teaspoons coarse kosher salt", "1/4 teaspoon ground black pepper", "1 quart vegetable oil"]}'::jsonb);
INSERT INTO recipes(name, description, image, prep_time, cook_time, contributor, instructions, ingredients) VALUES ('Lobster Tail with Garlic Lemon Butter', '"This is the only Lobster tails recipe you''ll ever need and it''s surprisingly easy! The broiled lobster meat is crazy tender, juicy and each bite is tantalizingly flavorful, especially after dipping into the warm garlic lemon butter."', 'https://sweetcsdesigns.com/wp-content/uploads/2017/12/Garlic-Butter-Lobster-recipe-image.jpg', '15', '15', 'NatashasKitchen', '{"steps": ["1. Place rack in center of oven so the meaty tops of your lobster tails will be at least 6 inches from the top heating element. Preheat oven to broil on high heat.", "2. In a small bowl, stir together marinade ingredients: 1 Tbsp parsley, 2 garlic cloves, 1 tsp dijon, 1/4 tsp salt, 1/8 tsp pepper, 1 1/2 Tbsp olive oil and 1 1/2 Tbsp lemon juice.", "3. Place butterflied Lobster Tails into a 9x13 or 8x12 roasting pan. Divide marinade evenly over the tops of each lobster tail and dot each tail all over with 1/2 Tbsp of butter cut into smaller pieces.", "4. Broil lobster tails on high heat 10-11 min (or according to the lobster tail size - see chart). When done, lobster meat should be opaque and white in the center and register 145ËšF on an instant read thermometer. Transfer to serving platter and garnish with parsley if desired.", "5. To make the Garlic Lemon Butter: drain drippings from the roasting pan into a small saucepan and combine with remaining 2 Tbsp butter, bring to a simmer and remove from heat. Pour over lobster tails or divide into ramekins and use as dipping sauce."]}'::jsonb, '{"items": ["4 lobster tails 5 to 6 oz each", "1 Tbsp fresh parsley very finely chopped, plus more for garnish", "2 garlic cloves pressed", "1 tsp dijon mustard", "1/4 tsp salt", "1/8 tsp black pepper", "1 1/2 Tbsp olive oil", "1 1/2 Tbsp fresh lemon juice", "4 Tbsp unsalted butter divided"]}'::jsonb);